    public static 'SEND_MESSAGES' = 'SEND_MESSAGES'
    public static 'SEND_TTS_MESSAGES' = 'SEND_TTS_MESSAGES'
    public static 'MANAGE_MESSAGES' = 'MANAGE_MESSAGES'
    public static 'EMBED_LINKS' = 'EMBED_LINKS'
