export class Reason {
    constructor(noun: 'create' | 'update' | 'delete', database: 'PinguGuild' | 'PinguUser', reason: string) {
        this.reason = reason.substring(0, 1).toUpperCase() + reason.substring(1).toLowerCase();

        if (!['.','!','?',':'].includes(this.reason[this.reason.length - 1])) 
            this.reason += '.';

        this.succMsg = `Successfully ${noun}d ${database}: ${this.reason}`;
        this.errMsg = `Failed to ${noun} ${database}: ${this.reason}`;
    }

    private reason: string;
    public succMsg: string;
    public errMsg: string;
}