import { Client, User } from "discord.js"

export function Developers(client: Client) {
    let obj = {
        Danho() { return client.users.cache.get('245572699894710272') },
        SynthySytro() { return client.users.cache.get('405331883157880846') },
        Slohtman() { return client.users.cache.get('290131910091603968') },
        DefilerOfCats() { return client.users.cache.get('803903863706484756') },

        includes(id: string) {
            return Object
                .keys(obj)
                .filter(v => v != 'includes')
                .map(key => obj[key] && obj[key].id)
                .includes(id);
        }
    }
    return obj;
}
export function isPinguDev(user: User) {
    return Developers(user.client).includes(user.id);
}