import { Client, User, Collection } from "discord.js"

export const Developers = new Collection<string, User>();
export async function CacheDevelopers(client: Client){
    const developers = {
        Danho: '245572699894710272',
        SynthySytro: '405331883157880846',
        Slothman: '290131910091603968',
        DefilerOfCats: '803903863706484756'
    }

    const devIDs = Object.keys(developers).map(dev => developers[dev]);

    for (const id of devIDs) {
        let user = await client.users.fetch(id);
        Developers.set(id, user);
    }
    return Developers;
}


export function isPinguDev(user: User) {
    return Developers.get(user.id) != null;
}