import { PItem } from "../../../database/json/PItem";

export type AchievementType = 'GUILD' | 'USER'
export type UserAchievementTypes = 'CHAT' | 'COMMAND'
export type GuildAchievementTypes = UserAchievementTypes | 'VOICE'

class AchievementBase extends PItem {
    constructor(id: number, name: string, type: AchievementType, description: string, ) {
        super({ id: id.toString(), name });
        this.type = type;
        this.description = description;
    }

    public type: AchievementType
    public description: string
}

export class Achievement extends PItem {
    constructor(id: number, name: string, type: AchievementType, description: string, ) {
        super({ id: id.toString(), name });
    }

    public static Achievements: Achievement[] = [
        new Achievement(1, )
    ]
}